from web_client_advance import WebClient_advance  # WebClient_advance sınıfını import ediyoruz


def test_external_server():
    print("\n---- Dış Web Sunucusu Test Ediliyor ----")

    # Örnek dış web sunucusuna istek göndermek için nesne oluşturuyoruz
    external_client = WebClient_advance("example.com", 80)  # example.com kullanarak dış sunucuya istek göndereceğiz

    try:
        # WebClient_advance sınıfındaki web_client_advance fonksiyonu ile istek gönderiyoruz
        response = external_client.web_client_advance("/")  # Ana sayfaya ("/") istek gönderiyoruz
        if response:
            print("\n--- 📥 Dış Sunucudan Gelen Cevap ---\n")
            print(response)  # Dış sunucudan gelen yanıtı yazdırıyoruz
    except Exception as e:
        print(f"❌ Dış sunucuya bağlanırken bir hata oluştu: {e}")


# Dış sunucuyu test et
test_external_server()
